import React, { useState } from "react";
import backgroundImage from "../images/bg-1.jpg"; // Import the image

const Home = () => {
  const [hoveredLink, setHoveredLink] = useState(null); // Tracks the hovered link

  return (
    <div
      className="min-h-screen bg-cover bg-center text-white flex flex-col justify-between"
      style={{
        backgroundImage: `url(${backgroundImage})`,
      }}
    >
      {/* Navigation Bar */}
      <nav className="w-full flex justify-between items-center px-8 py-4 bg-black bg-opacity-70">
        {/* Logo */}
        <h1 className="text-4xl font-extrabold cursor-pointer transform transition duration-200 hover:scale-110 hover:text-red-500">
          <a href="#home">GHABATY</a>
        </h1>

        {/* Navigation Links */}
        <ul className="flex gap-8">
          {["Home", "Alerts", "Reports", "Contact", "About Us"].map((link) => (
            <li
              key={link}
              className={`p-1 ${
                hoveredLink === null && link === "Home"
                  ? "border-b-4 border-red-500 text-red-500" // Default state for "Home"
                  : hoveredLink === link
                  ? "border-b-4 border-red-500 text-red-500" // Highlight hovered link
                  : "hover:border-b-4 hover:border-red-500 hover:text-red-500"
              } transition-all duration-200`}
              onMouseEnter={() => setHoveredLink(link)} // Update hovered link
              onMouseLeave={() => setHoveredLink(null)} // Reset on mouse leave
            >
              <a href={`#${link.toLowerCase().replace(/\s+/g, "-")}`}>{link}</a>
            </li>
          ))}
        </ul>

        {/* Sign Up Button */}
        <button className="px-4 py-2 bg-red-600 rounded-lg transform hover:bg-red-700 hover:-translate-y-1 transition-all duration-200">
          Sign Up
        </button>
      </nav>

      {/* Hero Section */}
      <div className="flex-1 flex flex-col justify-center items-center text-center px-6">
        <div className="bg-black bg-opacity-60 p-8 rounded-lg drop-shadow-lg max-w-3xl">
          <h1 className="text-5xl font-bold mb-6 transform transition duration-200 hover:scale-105">
            Stay Alert, Stay Safe
          </h1>
          <p className="text-lg mb-8 leading-relaxed">
            Get real-time updates and insights to help protect your communities
            from wildfires. Together, we can make a difference.
          </p>
          <button className="px-6 py-3 bg-red-500 rounded-lg text-lg font-medium hover:bg-red-600 transform hover:-translate-y-1 transition-all duration-200">
            Get Started
          </button>
        </div>
      </div>

      {/* Footer */}
      <footer className="w-full py-6 bg-black bg-opacity-90 text-center text-sm">
        <ul className="flex justify-center gap-6 mb-4">
          {["Privacy Policy", "Terms of Service", "Help"].map((footerLink) => (
            <li
              key={footerLink}
              className="hover:text-red-500 hover:underline transition-all duration-200"
            >
              <a href={`#${footerLink.toLowerCase().replace(/\s+/g, "-")}`}>
                {footerLink}
              </a>
            </li>
          ))}
        </ul>
        <p>Â© 2024 Ghabaty. All rights reserved.</p>
      </footer>
    </div>
  );
};

export default Home;
