import React, { useState, useCallback } from "react";
import { Link, useLocation } from "react-router-dom";
import backgroundImage from "../images/reports-bg.jpg"; // Path to the background image

const Reports = () => {
  const [reports, setReports] = useState(
    JSON.parse(localStorage.getItem("reports")) || []
  );
  const [reportText, setReportText] = useState("");
  const [showReports, setShowReports] = useState(false);
  const [hoveredLink, setHoveredLink] = useState(null);
  const location = useLocation();

  const handleReportSubmit = useCallback(
    (e) => {
      e.preventDefault();

      if (reportText.trim() === "") return;

      const newReport = {
        text: reportText,
        date: new Date().toLocaleString(),
      };

      const updatedReports = [newReport, ...reports];
      setReports(updatedReports);
      localStorage.setItem("reports", JSON.stringify(updatedReports));
      setReportText(""); // Clear the textarea
    },
    [reports, reportText]
  ); // Use useCallback to avoid unnecessary re-renders

  const toggleShowReports = useCallback(() => {
    setShowReports((prevState) => !prevState);
  }, []); // Use useCallback for state toggle function

  return (
    <div
      className="min-h-screen bg-cover bg-center text-white flex flex-col"
      style={{
        backgroundImage: `linear-gradient(to bottom, rgba(0, 0, 0, 0.6), rgba(0, 0, 0, 0.8)), url(${backgroundImage})`,
      }}
    >
      {/* Navigation Bar */}
      <nav className="w-full flex justify-between items-center px-8 py-4 bg-black bg-opacity-70 shadow-md">
        <h1 className="text-4xl font-extrabold cursor-pointer transform transition duration-200 hover:scale-110 hover:text-red-400">
          <Link to="/">GHABATY</Link>
        </h1>
        <ul className="flex gap-8 text-lg">
          {["Home", "Alerts", "Reports", "Contact", "About Us"].map((link) => (
            <li
              key={link}
              className={`p-1 ${
                location.pathname.includes(link.toLowerCase()) &&
                hoveredLink !== link
                  ? "border-b-4 border-red-400 text-red-400"
                  : hoveredLink === link
                  ? "border-b-4 border-red-400 text-red-400"
                  : "hover:border-b-4 hover:border-red-400 hover:text-red-400"
              } transition-all duration-200`}
              onMouseEnter={() => setHoveredLink(link)}
              onMouseLeave={() => setHoveredLink(null)}
            >
              <Link
                to={`/${link.toLowerCase().replace(/\s+/g, "-")}`}
                className="transition-all duration-200"
              >
                {link}
              </Link>
            </li>
          ))}
        </ul>
      </nav>

      {/* Main Section */}
      <section className="flex flex-wrap lg:flex-nowrap justify-between max-w-7xl mx-auto mt-12 gap-12 p-6">
        {/* Form Section */}
        <div className="form-container bg-black bg-opacity-80 p-8 rounded-lg shadow-lg w-full lg:w-5/12">
          <h1 className="text-3xl font-bold mb-6 text-left text-red-400">
            Submit a Report
          </h1>
          <form
            id="report-form"
            onSubmit={handleReportSubmit}
            className="flex flex-col space-y-6"
          >
            <textarea
              id="report-details"
              placeholder="Describe the issue..."
              value={reportText}
              onChange={(e) => setReportText(e.target.value)}
              required
              className="w-full h-48 px-6 py-4 text-gray-900 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-red-500 text-lg"
            />
            <button
              type="submit"
              className="px-6 py-3 bg-gradient-to-r from-red-400 to-red-600 text-white rounded-full text-lg font-medium hover:from-red-500 hover:to-red-700 hover:shadow-lg transition-all duration-200"
            >
              Submit Report
            </button>
          </form>
        </div>

        {/* Latest Reports Section */}
        <div className="latest-reports bg-black bg-opacity-80 p-8 rounded-lg shadow-lg w-full lg:w-7/12">
          <h2 className="text-2xl font-bold mb-6 text-left text-red-400">
            {showReports ? "Latest Reports" : "View Latest Reports"}
          </h2>
          {showReports ? (
            reports.length === 0 ? (
              <p className="text-gray-300">No reports submitted yet.</p>
            ) : (
              <ul className="space-y-6">
                {reports.map((report, index) => (
                  <li
                    key={index}
                    className="p-6 bg-gray-900 rounded-lg shadow-sm hover:shadow-md border border-gray-700 transition-transform transform hover:-translate-y-1"
                  >
                    <p className="text-gray-200">{report.text}</p>
                    <p className="text-sm text-gray-400 mt-2">
                      Submitted on: {report.date}
                    </p>
                  </li>
                ))}
              </ul>
            )
          ) : (
            <button
              onClick={toggleShowReports}
              className="text-red-400 hover:underline text-lg transition-all duration-200 block mx-auto"
            >
              Show Latest Reports
            </button>
          )}
        </div>
      </section>

      {/* Footer */}
      <footer className="w-full py-6 bg-black bg-opacity-90 text-center text-sm text-gray-400 mt-8">
        <ul className="flex justify-center gap-6 mb-4">
          {["Privacy Policy", "Terms of Service", "Help"].map((footerLink) => (
            <li
              key={footerLink}
              className="hover:text-red-400 hover:underline transition-all duration-200"
            >
              <Link to={`/${footerLink.toLowerCase().replace(/\s+/g, "-")}`}>
                {footerLink}
              </Link>
            </li>
          ))}
        </ul>
        <p>© 2024 Ghabaty. All rights reserved.</p>
      </footer>
    </div>
  );
};

export default Reports;
