import React, { useState } from "react";
import { Link, useLocation } from "react-router-dom";
import backgroundImage from "../images/reports-bg.jpg"; // Background image path

const Reports = () => {
  const [reports, setReports] = useState(
    JSON.parse(localStorage.getItem("reports")) || []
  );
  const [showReports, setShowReports] = useState(false);
  const [reportText, setReportText] = useState("");
  const [hoveredLink, setHoveredLink] = useState(null);

  const location = useLocation();

  const handleSubmit = (e) => {
    e.preventDefault();

    if (reportText.trim() === "") return;

    const newReport = {
      text: reportText,
      date: new Date().toLocaleString(),
    };

    const updatedReports = [newReport, ...reports];
    setReports(updatedReports);
    localStorage.setItem("reports", JSON.stringify(updatedReports));
    setReportText(""); // Clear the textarea
  };

  return (
    <div
      className="min-h-screen bg-cover bg-center text-white flex flex-col"
      style={{
        backgroundImage: `url(${backgroundImage})`,
      }}
    >
      {/* Navigation Bar */}
      <nav className="w-full flex justify-between items-center px-8 py-4 bg-black bg-opacity-70">
        <h1 className="text-4xl font-extrabold cursor-pointer transform transition duration-200 hover:scale-110 hover:text-red-400">
          <Link to="/">GHABATY</Link>
        </h1>
        <ul className="flex gap-8 text-lg">
          {["Home", "Alerts", "Reports", "Contact", "About Us"].map((link) => (
            <li
              key={link}
              className={`p-1 ${
                location.pathname.includes(link.toLowerCase()) &&
                hoveredLink !== link
                  ? "border-b-4 border-red-400 text-red-400"
                  : hoveredLink === link
                  ? "border-b-4 border-red-400 text-red-400"
                  : "hover:border-b-4 hover:border-red-400 hover:text-red-400"
              } transition-all duration-200`}
              onMouseEnter={() => setHoveredLink(link)}
              onMouseLeave={() => setHoveredLink(null)}
            >
              <Link
                to={`/${link.toLowerCase().replace(/\s+/g, "-")}`}
                className="transition-all duration-200"
              >
                {link}
              </Link>
            </li>
          ))}
        </ul>
        <button className="px-5 py-2 bg-gradient-to-r from-red-400 to-red-600 rounded-full text-lg font-medium hover:from-red-500 hover:to-red-700 hover:shadow-lg transform hover:-translate-y-1 transition-all duration-200">
          Sign Up
        </button>
      </nav>

      {/* Form Section */}
      <section className="form-container max-w-3xl mx-auto bg-black bg-opacity-80 p-8 rounded-lg shadow-lg mt-12">
        <h1 className="text-3xl font-bold mb-4 text-center text-red-400">
          Submit a Report
        </h1>
        <form
          id="report-form"
          onSubmit={handleSubmit}
          className="flex flex-col space-y-4"
        >
          <textarea
            id="report-details"
            placeholder="Describe the issue..."
            value={reportText}
            onChange={(e) => setReportText(e.target.value)}
            required
            className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-red-500"
            rows="5"
          ></textarea>
          <button
            type="submit"
            className="px-6 py-3 bg-gradient-to-r from-red-400 to-red-600 text-white rounded-full hover:from-red-500 hover:to-red-700 hover:shadow-lg transition-all duration-200"
          >
            Submit Report
          </button>
        </form>
      </section>

      {/* Link to Show Latest Reports */}
      <section className="mt-8 text-center">
        <button
          onClick={() => setShowReports(!showReports)}
          className="text-red-400 hover:underline transition-all duration-200"
        >
          {showReports ? "Hide Latest Reports" : "View Latest Reports"}
        </button>
      </section>

      {/* Latest Reports Section */}
      {showReports && (
        <section className="latest-reports max-w-3xl mx-auto mt-8 bg-black bg-opacity-80 p-8 rounded-lg shadow-lg">
          <h2 className="text-2xl font-bold mb-4 text-red-400">
            Latest Reports
          </h2>
          {reports.length === 0 ? (
            <p className="text-gray-300">No reports submitted yet.</p>
          ) : (
            <ul className="space-y-4">
              {reports.map((report, index) => (
                <li
                  key={index}
                  className="p-4 bg-gray-900 rounded-lg shadow-sm hover:shadow-md border border-gray-700 transition-transform transform hover:-translate-y-1"
                >
                  <p className="text-gray-200">{report.text}</p>
                  <p className="text-sm text-gray-400 mt-2">
                    Submitted on: {report.date}
                  </p>
                </li>
              ))}
            </ul>
          )}
        </section>
      )}

      {/* Footer */}
      <footer className="w-full py-6 bg-black bg-opacity-80 text-center text-sm text-gray-400 mt-8">
        <ul className="flex justify-center gap-6 mb-4">
          {["Privacy Policy", "Terms of Service", "Help"].map((footerLink) => (
            <li
              key={footerLink}
              className="hover:text-red-400 hover:underline transition-all duration-200"
            >
              <Link to={`/${footerLink.toLowerCase().replace(/\s+/g, "-")}`}>
                {footerLink}
              </Link>
            </li>
          ))}
        </ul>
        <p>Â© 2024 Ghabaty. All rights reserved.</p>
      </footer>
    </div>
  );
};

export default Reports;
