import React, { useState } from "react";
import backgroundImage from "../images/bg-1.jpg"; // Import the image

const Home = () => {
  const [activeLink, setActiveLink] = useState("Home");
  const [hoveredLink, setHoveredLink] = useState(null);

  return (
    <div
      className="min-h-screen bg-cover bg-center text-white flex flex-col justify-between"
      style={{
        backgroundImage: `url(${backgroundImage})`,
      }}
    >
      {/* Navigation Bar */}
      <nav className="w-full flex justify-between items-center px-8 py-4 bg-black bg-opacity-70">
        <h1 className="text-4xl font-extrabold hover:scale-110 transform transition duration-500 cursor-pointer">
          <a href="#home" onClick={() => setActiveLink("Home")}>
            GHABATY
          </a>
        </h1>
        <ul className="flex gap-8">
          {["Home", "Alerts", "Reports", "Contact", "About Us"].map((link) => (
            <li
              key={link}
              className={`pb-1 ${
                activeLink === link && hoveredLink !== null
                  ? "" // Hide border when another link is hovered
                  : activeLink === link
                  ? "border-b-4 border-red-500 text-red-500"
                  : ""
              } ${
                hoveredLink === link
                  ? "hover:border-b-4 hover:border-red-500 text-red-500"
                  : "hover:translate-y-1 hover:text-red-500 hover:border-b-4 hover:border-red-500"
              } transform transition-all duration-700`}
              onMouseEnter={() => setHoveredLink(link)}
              onMouseLeave={() => setHoveredLink(null)}
            >
              <a
                href={`#${link.toLowerCase().replace(/\s+/g, "-")}`}
                onClick={() => setActiveLink(link)}
              >
                {link}
              </a>
            </li>
          ))}
        </ul>
        <button className="px-4 py-2 bg-red-600 rounded-lg hover:bg-red-700 transform hover:-translate-y-1 transition-all duration-500 shadow-lg hover:shadow-red-500/50">
          Sign Up
        </button>
      </nav>

      {/* Hero Section */}
      <div className="flex-1 flex flex-col justify-center items-center text-center px-6">
        <div className="bg-black bg-opacity-60 p-8 rounded-lg drop-shadow-lg max-w-3xl">
          <h1 className="text-5xl font-bold mb-6 hover:scale-105 transform transition duration-700">
            Stay Alert, Stay Safe
          </h1>
          <p className="text-lg mb-8 leading-relaxed">
            Get real-time updates and insights to help protect your communities
            from wildfires. Together, we can make a difference.
          </p>
          <button className="px-6 py-3 bg-red-500 rounded-lg text-lg font-medium hover:bg-red-600 transform hover:-translate-y-1 transition-all duration-500 shadow-lg hover:shadow-red-500/50">
            Get Started
          </button>
        </div>
      </div>

      {/* Features Section */}
      <section className="py-12 bg-gray-800 text-white">
        <div className="container mx-auto px-6 text-center">
          <h2 className="text-3xl font-bold mb-8">Why Choose Ghabaty?</h2>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
            {[
              {
                title: "Real-Time Alerts",
                description:
                  "Stay updated with real-time wildfire alerts to keep your community safe.",
              },
              {
                title: "Detailed Reports",
                description:
                  "Access detailed insights and reports for effective decision-making.",
              },
              {
                title: "Community Focused",
                description:
                  "Our tools are built to protect and empower local communities.",
              },
            ].map((feature, idx) => (
              <div
                key={idx}
                className="p-6 bg-gray-700 rounded-lg shadow-lg hover:shadow-red-500/50 transform hover:-translate-y-2 transition duration-500"
              >
                <h3 className="text-xl font-bold mb-4">{feature.title}</h3>
                <p>{feature.description}</p>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Footer */}
      <footer className="w-full py-6 bg-black bg-opacity-90 text-center text-sm">
        <ul className="flex justify-center gap-6 mb-4">
          {["Privacy Policy", "Terms of Service", "Help"].map((footerLink) => (
            <li
              key={footerLink}
              className="hover:text-red-500 hover:underline transition duration-500"
            >
              <a href={`#${footerLink.toLowerCase().replace(/\s+/g, "-")}`}>
                {footerLink}
              </a>
            </li>
          ))}
        </ul>
        <p>© 2024 Ghabaty. All rights reserved.</p>
      </footer>
    </div>
  );
};

export default Home;
