import React, { useState } from "react";
import { AiOutlineClose, AiOutlineMenu } from "react-icons/ai";

export default function Navbar() {
  const [activeLink, setActiveLink] = useState("Home");
  const [isNavVisible, setIsNavVisible] = useState(false);

  const handleNav = () => {
    setIsNavVisible(!isNavVisible);
  };

  return (
    <div className="fixed top-0 left-0 right-0 z-50 bg-white shadow-md">
      <div className="flex justify-between items-center h-24 max-w-[1240px] mx-auto px-4 text-black text-lg">
        {/* Logo */}
        <h1 className="text-black text-3xl font-bold whitespace-nowrap md:text-4xl">
          Morocco <span className="text-blue-500">HPC</span>
        </h1>

        {/* Desktop Menu */}
        <ul className="hidden md:flex items-center font-medium space-x-6 text-lg">
          {["Home", "Conference", "Webinars", "Newsletter"].map((link) => (
            <li
              key={link}
              className={`p-2 ${
                activeLink === link
                  ? "border-b-2 border-blue-500 text-blue-500"
                  : ""
              } hover:border-b-2 hover:border-blue-500 hover:text-blue-500 transition-all duration-300`}
              onClick={() => setActiveLink(link)}
            >
              <a href={`#${link.toLowerCase()}`}>{link}</a>
            </li>
          ))}
          <li>
            <button className="px-6 py-2 bg-blue-500 text-white rounded-full hover:bg-blue-600 hover:scale-105 transition-all duration-300">
              Join Us
            </button>
          </li>
        </ul>

        {/* Mobile Menu Icon */}
        <div className="flex items-center md:hidden">
          <button className="px-4 py-2 bg-blue-500 text-white rounded-full hover:bg-blue-600 transition duration-300">
            Join Us
          </button>
          <div onClick={handleNav} className="cursor-pointer ml-4">
            {isNavVisible ? (
              <AiOutlineClose size={24} />
            ) : (
              <AiOutlineMenu size={24} />
            )}
          </div>
        </div>
      </div>

      {/* Mobile Menu Backdrop */}
      <div
        className={`${
          isNavVisible ? "fixed inset-0 bg-black bg-opacity-50 z-40" : "hidden"
        }`}
        onClick={handleNav}
      ></div>

      {/* Mobile Menu */}
      <div
        className={`fixed left-0 top-0 h-full w-[70%] max-w-[400px] bg-white shadow-lg transform ${
          isNavVisible ? "translate-x-0" : "-translate-x-full"
        } transition-transform duration-500 ease-in-out z-50`}
      >
        {/* Mobile Logo */}
        <div className="flex items-center justify-center pt-10">
          <h1 className="text-black font-bold text-3xl">
            Morocco <span className="text-blue-500">HPC</span>
          </h1>
        </div>

        {/* Mobile Menu Links */}
        <ul className="flex flex-col items-center pt-16 space-y-6 text-lg font-medium">
          {["Home", "Conference", "Webinars", "Newsletter"].map((link) => (
            <li
              key={link}
              className={`${
                activeLink === link
                  ? "border-b-2 border-blue-500 text-blue-500"
                  : "hover:border-b-2 hover:border-blue-500 hover:text-blue-500"
              } transition-all duration-300`}
              onClick={() => {
                setActiveLink(link);
                handleNav();
              }}
            >
              <a href={`#${link.toLowerCase()}`}>{link}</a>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}
