import React, { useState } from "react";

const Reports = () => {
  const [reports, setReports] = useState(
    JSON.parse(localStorage.getItem("reports")) || []
  );
  const [showReports, setShowReports] = useState(false);
  const [reportText, setReportText] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();

    if (reportText.trim() === "") return;

    const newReport = {
      text: reportText,
      date: new Date().toLocaleString(),
    };

    const updatedReports = [newReport, ...reports];
    setReports(updatedReports);
    localStorage.setItem("reports", JSON.stringify(updatedReports));
    setReportText(""); // Clear the textarea
  };

  return (
    <div className="min-h-screen bg-gray-100 text-gray-800 p-6">
      {/* Form Section */}
      <section className="form-container max-w-2xl mx-auto bg-white p-6 rounded-lg shadow-md">
        <h1 className="text-2xl font-bold mb-4">Submit a Report</h1>
        <form
          id="report-form"
          onSubmit={handleSubmit}
          className="flex flex-col space-y-4"
        >
          <textarea
            id="report-details"
            placeholder="Describe the issue..."
            value={reportText}
            onChange={(e) => setReportText(e.target.value)}
            required
            className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-red-500"
            rows="5"
          ></textarea>
          <button
            type="submit"
            className="px-4 py-2 bg-red-500 text-white rounded-lg hover:bg-red-600 transition"
          >
            Submit Report
          </button>
        </form>
      </section>

      {/* Link to Show Latest Reports */}
      <section className="mt-6 text-center">
        <button
          onClick={() => setShowReports(!showReports)}
          className="text-red-500 hover:underline transition"
        >
          {showReports ? "Hide Latest Reports" : "View Latest Reports"}
        </button>
      </section>

      {/* Latest Reports Section */}
      {showReports && (
        <section className="latest-reports max-w-2xl mx-auto mt-6 bg-white p-6 rounded-lg shadow-md">
          <h2 className="text-xl font-bold mb-4">Latest Reports</h2>
          {reports.length === 0 ? (
            <p className="text-gray-500">No reports submitted yet.</p>
          ) : (
            <ul className="space-y-4">
              {reports.map((report, index) => (
                <li
                  key={index}
                  className="p-4 bg-gray-100 rounded-lg shadow-sm border"
                >
                  <p className="text-gray-800">{report.text}</p>
                  <p className="text-sm text-gray-500 mt-2">
                    Submitted on: {report.date}
                  </p>
                </li>
              ))}
            </ul>
          )}
        </section>
      )}
    </div>
  );
};

export default Reports;
